mapper.select:
select exam_papers.*,exam_course.id courseId,exam_course.course_name,exam_professional.id professionalId,exam_professional.professional_name,exam_student.id studentId,exam_student.class_id classId from exam_papers left join exam_papers_course on exam_papers.id = exam_papers_course.papers_id left join exam_course on exam_papers_course.course_id = exam_course.id join exam_professional on exam_papers_course.professional_id = exam_professional.id join exam_papers_user on exam_papers.id = exam_papers_user.papers_id left join exam_student on exam_papers_user.user_id = exam_student.id where 1=1
and name like concat('%',#{name},'%')
and papers_name like concat('%',#{papersName},'%')
and type = #{type}
and exam_course.id = #{course_id}

select exam_papers.*,exam_course.id courseId,exam_course.course_name,exam_professional.id professionalId,exam_professional.professional_name,exam_student.id studentId,exam_student.class_id classId from exam_papers left join exam_papers_course on exam_papers.id = exam_papers_course.papers_id left join exam_course on exam_papers_course.course_id = exam_course.id join exam_professional on exam_papers_course.professional_id = exam_professional.id join exam_papers_user on exam_papers.id = exam_papers_user.papers_id left join exam_student on exam_papers_user.user_id = exam_student.id where ispublish=1
and name like concat('%',#{name},'%')
and papers_name like concat('%',#{papersName},'%')
and type = #{type}
and exam_course.id = #{course_id}

select exam_papers.*, exam_student.stu_name, exam_class.id classId, exam_class.class_name className, exam_student.id studentId from exam_papers join exam_papers_user on exam_papers.id = exam_papers_user.papers_id join exam_student on exam_papers_user.user_id = exam_student.id join exam_class on exam_student.class_id = exam_class.id where 1=1
and exam_papers.type = #{type}
and exam_class.id = #{classId}
and exam_papers.name like concat('%',#{papersName},'%')
