mapper.select:
select id,login_name,real_name,department,POSITION,mobile_phone,create_time,remark from exam_sys_user where 1=1

    and department like concat('%',#{department},'%')


    and position = #{position}


    limit #{page},#{pageSize}

mapper.insert:
insert into exam_sys_user(login_name,real_name,login_password,department,

    position,


    mobile_phone,

create_time

    ,remark

) values(#{login_name},#{real_name},#{login_password},#{department}

    ,#{position}


    ,#{mobile_phone}

, now()

    ,#{remark}

)

mapper.update:
update exam_sys_user set

    login_name = #{login_name}


    , real_name = #{real_name}


    , login_password = #{login_password}


    , department = #{department}


    , position = #{position}


    , mobile_phone = #{mobile_phone}


    , create_time = #{create_time}


    , remark = #{remark}

where id = #{id}
